import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { Calendar, Clock, Users, MapPin, Phone, Mail } from 'lucide-react';
import { reservationAPI, customerAPI } from '../services/api';
import toast from 'react-hot-toast';
import { Link } from 'react-router-dom';

const ReservationForm = () => {
  const isAuthenticated = localStorage.getItem('isAdminAuthenticated') === 'true';
  if (!isAuthenticated) {
    return (
      <div className="max-w-md mx-auto card mt-12 text-center">
        <h2 className="text-xl font-bold mb-4">Admin Login Required</h2>
        <p className="mb-4">You must be logged in as admin to make a reservation.</p>
        <Link to="/admin-login" className="btn-primary">Go to Admin Login</Link>
      </div>
    );
  }

  const [availableTables, setAvailableTables] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedTable, setSelectedTable] = useState(null);
  
  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors }
  } = useForm();

  const watchDate = watch('date');
  const watchTime = watch('time');
  const watchPartySize = watch('partySize');

  // Check availability when date, time, or party size changes
  useEffect(() => {
    if (watchDate && watchTime && watchPartySize) {
      checkAvailability();
    }
  }, [watchDate, watchTime, watchPartySize]);

  const checkAvailability = async () => {
    try {
      const response = await reservationAPI.getAvailableTables({
        restaurant_id: 'rest_001', // Default restaurant
        party_size: watchPartySize,
        reservation_time: `${watchDate}T${watchTime}:00.000Z`
      });
      setAvailableTables(response.data.availableTables || []);
    } catch (error) {
      console.error('Error checking availability:', error);
    }
  };

  const onSubmit = async (data) => {
    if (!selectedTable) {
      toast.error('Please select a table');
      return;
    }

    setIsLoading(true);
    try {
      // Create customer first
      const customerData = {
        name: data.name,
        email: data.email,
        phone_number: data.phone,
        preferences: {
          dietary_restrictions: data.dietaryRestrictions || '',
          seating_preference: data.seatingPreference || 'any'
        }
      };

      const customerResponse = await customerAPI.create(customerData);
      const customerId = customerResponse.data.customer.customer_id;

      // Create reservation
      const reservationData = {
        customer_id: customerId,
        restaurant_id: 'rest_001',
        table_id: selectedTable.table_id,
        reservation_time: `${data.date}T${data.time}:00.000Z`,
        party_size: parseInt(data.partySize),
        special_requests: data.specialRequests ? [data.specialRequests] : []
      };

      const reservationResponse = await reservationAPI.create(reservationData);
      
      toast.success('Reservation created successfully!');
      
      // Reset form
      setValue('name', '');
      setValue('email', '');
      setValue('phone', '');
      setValue('date', '');
      setValue('time', '');
      setValue('partySize', '');
      setValue('dietaryRestrictions', '');
      setValue('seatingPreference', '');
      setValue('specialRequests', '');
      setSelectedTable(null);
      setAvailableTables([]);
      
    } catch (error) {
      console.error('Error creating reservation:', error);
      toast.error('Failed to create reservation. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const getMinDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Make a Reservation</h1>
        <p className="text-gray-600">
          Book your table with our easy-to-use reservation system
        </p>
      </div>

      <div className="grid lg:grid-cols-2 gap-8">
        {/* Reservation Form */}
        <div className="card">
          <h2 className="text-xl font-semibold mb-6 flex items-center">
            <Calendar className="w-5 h-5 mr-2" />
            Reservation Details
          </h2>

          <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
            {/* Personal Information */}
            <div className="space-y-4">
              <h3 className="font-medium text-gray-900">Personal Information</h3>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Full Name *
                </label>
                <input
                  type="text"
                  {...register('name', { required: 'Name is required' })}
                  className="input-field"
                  placeholder="Enter your full name"
                />
                {errors.name && (
                  <p className="text-red-600 text-sm mt-1">{errors.name.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email *
                </label>
                <input
                  type="email"
                  {...register('email', { 
                    required: 'Email is required',
                    pattern: {
                      value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                      message: 'Invalid email address'
                    }
                  })}
                  className="input-field"
                  placeholder="Enter your email"
                />
                {errors.email && (
                  <p className="text-red-600 text-sm mt-1">{errors.email.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Phone Number *
                </label>
                <input
                  type="tel"
                  {...register('phone', { required: 'Phone number is required' })}
                  className="input-field"
                  placeholder="Enter your phone number"
                />
                {errors.phone && (
                  <p className="text-red-600 text-sm mt-1">{errors.phone.message}</p>
                )}
              </div>
            </div>

            {/* Reservation Details */}
            <div className="space-y-4">
              <h3 className="font-medium text-gray-900">Reservation Details</h3>
              
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Date *
                  </label>
                  <input
                    type="date"
                    {...register('date', { required: 'Date is required' })}
                    className="input-field"
                    min={getMinDate()}
                  />
                  {errors.date && (
                    <p className="text-red-600 text-sm mt-1">{errors.date.message}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Time *
                  </label>
                  <select
                    {...register('time', { required: 'Time is required' })}
                    className="input-field"
                  >
                    <option value="">Select time</option>
                    <option value="11:00">11:00 AM</option>
                    <option value="11:30">11:30 AM</option>
                    <option value="12:00">12:00 PM</option>
                    <option value="12:30">12:30 PM</option>
                    <option value="13:00">1:00 PM</option>
                    <option value="13:30">1:30 PM</option>
                    <option value="17:00">5:00 PM</option>
                    <option value="17:30">5:30 PM</option>
                    <option value="18:00">6:00 PM</option>
                    <option value="18:30">6:30 PM</option>
                    <option value="19:00">7:00 PM</option>
                    <option value="19:30">7:30 PM</option>
                    <option value="20:00">8:00 PM</option>
                    <option value="20:30">8:30 PM</option>
                    <option value="21:00">9:00 PM</option>
                    <option value="21:30">9:30 PM</option>
                  </select>
                  {errors.time && (
                    <p className="text-red-600 text-sm mt-1">{errors.time.message}</p>
                  )}
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Party Size *
                </label>
                <select
                  {...register('partySize', { required: 'Party size is required' })}
                  className="input-field"
                >
                  <option value="">Select party size</option>
                  <option value="1">1 person</option>
                  <option value="2">2 people</option>
                  <option value="3">3 people</option>
                  <option value="4">4 people</option>
                  <option value="5">5 people</option>
                  <option value="6">6 people</option>
                  <option value="7">7 people</option>
                  <option value="8">8 people</option>
                </select>
                {errors.partySize && (
                  <p className="text-red-600 text-sm mt-1">{errors.partySize.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Seating Preference
                </label>
                <select
                  {...register('seatingPreference')}
                  className="input-field"
                >
                  <option value="any">Any seating</option>
                  <option value="window">Window seat</option>
                  <option value="outdoor">Outdoor seating</option>
                  <option value="quiet">Quiet area</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Dietary Restrictions
                </label>
                <input
                  type="text"
                  {...register('dietaryRestrictions')}
                  className="input-field"
                  placeholder="e.g., Vegetarian, Gluten-free, etc."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Special Requests
                </label>
                <textarea
                  {...register('specialRequests')}
                  className="input-field"
                  rows="3"
                  placeholder="Any special requests or notes..."
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className="btn-primary w-full py-3 disabled:opacity-50"
            >
              {isLoading ? 'Creating Reservation...' : 'Book Reservation'}
            </button>
          </form>
        </div>

        {/* Table Selection */}
        <div className="space-y-6">
          {/* Available Tables */}
          {availableTables.length > 0 && (
            <div className="card">
              <h3 className="text-lg font-semibold mb-4 flex items-center">
                <Users className="w-5 h-5 mr-2" />
                Available Tables
              </h3>
              <div className="grid gap-3">
                {availableTables.map((table) => (
                  <div
                    key={table.table_id}
                    onClick={() => setSelectedTable(table)}
                    className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                      selectedTable?.table_id === table.table_id
                        ? 'border-primary-500 bg-primary-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <div className="flex justify-between items-center">
                      <div>
                        <h4 className="font-medium">Table {table.table_number}</h4>
                        <p className="text-sm text-gray-600">
                          {table.seating_capacity} seats • {table.location_type}
                        </p>
                      </div>
                      {selectedTable?.table_id === table.table_id && (
                        <div className="w-5 h-5 bg-primary-600 rounded-full flex items-center justify-center">
                          <div className="w-2 h-2 bg-white rounded-full"></div>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Restaurant Info */}
          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Restaurant Information</h3>
            <div className="space-y-3">
              <div className="flex items-center text-gray-600">
                <MapPin className="w-4 h-4 mr-2" />
                <span>123 Main Street, Downtown</span>
              </div>
              <div className="flex items-center text-gray-600">
                <Phone className="w-4 h-4 mr-2" />
                <span>(555) 123-4567</span>
              </div>
              <div className="flex items-center text-gray-600">
                <Mail className="w-4 h-4 mr-2" />
                <span>info@restaurant.com</span>
              </div>
              <div className="flex items-center text-gray-600">
                <Clock className="w-4 h-4 mr-2" />
                <span>Open: 11:00 AM - 11:00 PM</span>
              </div>
            </div>
          </div>

          {/* Instructions */}
          <div className="card bg-blue-50 border-blue-200">
            <h3 className="text-lg font-semibold mb-2 text-blue-900">How it works</h3>
            <ol className="text-sm text-blue-800 space-y-1">
              <li>1. Fill out your personal information</li>
              <li>2. Select your preferred date and time</li>
              <li>3. Choose your party size</li>
              <li>4. Select an available table</li>
              <li>5. Add any special requests</li>
              <li>6. Submit your reservation</li>
            </ol>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReservationForm;
